package com.example.oladipo.fyp.fragments;

import android.app.ProgressDialog;
import android.content.Context;
import android.net.ConnectivityManager;
import android.net.NetworkInfo;
import android.os.Bundle;
import android.support.annotation.NonNull;
import android.support.annotation.Nullable;
import android.support.v4.app.Fragment;
import android.support.v7.app.ActionBar;
import android.support.v7.app.AppCompatActivity;
import android.view.View;
import android.widget.Toast;

public class BaseFragment extends Fragment implements BaseContract.view {

    public ProgressDialog progressDialog;
    String token = "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..y0aN9_7vtfd80PA986MhUP9jo49GKwmVf-ZrZTWC980";


    public void setToolbarTitle (String title){
        ActionBar actionBar = ((AppCompatActivity)getActivity()).getSupportActionBar();
        actionBar.setTitle(title);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
    }

    @Override
    public void showloading(String message) {
        //if progress dialog is not being used or showing
        progressDialog = new ProgressDialog(getActivity());
            progressDialog.setMessage(message);
            progressDialog.setIndeterminate(true);
        progressDialog.show();
    }

    @Override
    public String getToken() {
        return token;
    }

    @Override
    public void showToast(String message) {
        Toast.makeText(getContext(), message, Toast.LENGTH_SHORT).show();
    }

    @Override
    public void hideLoading() {
        //if progress dialog is showing
        if (progressDialog!=null && progressDialog.isShowing()){
            progressDialog.dismiss();
        }
    }

    @Override
    public boolean isConnectionAvailable(Context context) {
        ConnectivityManager connectivityManager =
                (ConnectivityManager) context.getSystemService(Context.CONNECTIVITY_SERVICE);
        NetworkInfo activeNetworkInfo = connectivityManager.getActiveNetworkInfo();
        return activeNetworkInfo != null && activeNetworkInfo.isConnectedOrConnecting();
    }

}
